export function obternerDiferenciaYear(year){
    return new Date().getFullYear() - year;
}


// calcula el total a pagar segun la marca

export function calcularMarca(marca){
    let incremento;

    switch(marca){
        
        case 'europeo':
            incremento= 1.30;
            break
        case 'americano':
            incremento= 1.15;
        
        default:
                break;
    }
}
