    margin-top: 2rem;
    padding: 1rem;
    text-align: center;
`;

const Resultado =({cotizacion}) => {
    
    
    
    return (
         (cotizacion===0) ? <Mensaje>Elige marca , a√±o y tipo de seguro</Mensaje> : 
         
         <p>El total es: ${cotizacion} </p>
    );


}


export default Resultado;
